<?php	$description = 'Find out how exactly to use the configuration options with these code examples. For a carousel created with the carouFredSel-plugin.';	$keywords = 'code, examples, configuration, options, caroufredsel, help, support';	$title = 'Configuration - Code examples';	include_once '../functies/layout_pre.php';?>				<div id="usage">					<h1>Code examples for the configuration options</h1>					<p>A complete list of the available options, showing the default values:</p><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	circular	: true,	infinite	: true,	responsive	: false,	direction	: &quot;left&quot;,	width		: null,		// automatically calculated	height		: null,		// automatically calculated	align		: &quot;center&quot;,	padding		: 0,	synchronise	: null,	cookie		: false,	onCreate	: null,	items		: {		visible			: null,		//	automatically calculated		minimum			: null,		//	items.visible		start			: 0,		width			: null,		//	automatically calculated		height			: null,		//	automatically calculated		filter			: null	},	scroll		: {		items			: null,		//	items.visible		fx				: &quot;scroll&quot;,		easing			: &quot;swing&quot;,		duration		: 500,		pauseOnHover	: false,		queue			: false,		event			: &quot;click&quot;,		conditions		: null,		onBefore		: null,		onAfter			: null,		onEnd			: null	},	auto		: {		play			: true,		timeoutDuration	: null,		//	5 * auto.duration		delay			: 0,		items			: null,		//	scroll.items		fx				: null,		//	scroll.fx		easing			: null,		//	scroll.easing		duration		: null,		//	scroll.duration		pauseOnHover	: null,		//	scroll.pauseOnHover		pauseOnEvent	: false,		pauseOnResize	: true,		queue			: null,		//	scroll.queue		event			: null,		//	scroll.event		progress		: {			bar				: null,			updater			: $.fn.carouFredSel.progressbarUpdater,			interval		: 50		},		conditions		: null,		//	scroll.conditions		onBefore		: null,		//	scroll.onBefore		onAfter			: null,		//	scroll.onAfter		onEnd			: null,		//	scroll.onEnd		onTimeoutStart	: null,		onTimeoutEnd	: null,		onTimeoutPause	: null	},	prev		: {		button			: null,		key				: null,		items			: null,		//	scroll.items		fx				: null,		//	scroll.fx		easing			: null,		//	scroll.easing		duration		: null,		//	scroll.duration		pauseOnHover	: null,		//	scroll.pauseOnHover		queue			: null,		//	scroll.queue		event			: null,		//	scroll.event		conditions		: null,		//	scroll.conditions		onBefore		: null,		//	scroll.onBefore		onAfter			: null,		//	scroll.onAfter		onEnd			: null		//	scroll.onEnd	},	next		: {		button			: null,		key				: null,		items			: null,		//	scroll.items		fx				: null,		//	scroll.fx		easing			: null,		//	scroll.easing		duration		: null,		//	scroll.duration		pauseOnHover	: null,		//	scroll.pauseOnHover		queue			: null,		//	scroll.queue		event			: null,		//	scroll.event		conditions		: null,		//	scroll.conditions		onBefore		: null,		//	scroll.onBefore		onAfter			: null,		//	scroll.onAfter		onEnd			: null		//	scroll.onEnd	},	pagination	: {		container		: null,		keys			: false,		anchorBuilder	: null,		//	$.fn.carouFredSel.pageAnchorBuilder		items			: null,		//	items.visible		deviation		: 0,		fx				: null,		//	scroll.fx		easing			: null,		//	scroll.easing		duration		: null,		//	scroll.duration		pauseOnHover	: null,		//	scroll.pauseOnHover		queue			: null,		//	scroll.queue		event			: null,		//	scroll.event		conditions		: null,		//	scroll.conditions		onBefore		: null,		//	scroll.onBefore		onAfter			: null,		//	scroll.onAfter		onEnd			: null		//	scroll.onEnd	},	swipe		: {		onTouch			: false,		onMouse			: false,		options			: {},		items			: null,		//	scroll.items		fx				: null,		//	scroll.fx		easing			: null,		//	scroll.easing		duration		: null,		//	scroll.duration		pauseOnHover	: null,		//	scroll.pauseOnHover		queue			: null,		//	scroll.queue		conditions		: null,		//	scroll.conditions		onBefore		: null,		//	scroll.onBefore		onAfter			: null,		//	scroll.onAfter		onEnd			: null		//	scroll.onEnd	},	mousewheel	: {		items			: null,		//	scroll.items		fx				: null,		//	scroll.fx		easing			: null,		//	scroll.easing		duration		: null,		//	scroll.duration		pauseOnHover	: null,		//	scroll.pauseOnHover		queue			: null,		//	scroll.queue		conditions		: null,		//	scroll.conditions		onBefore		: null,		//	scroll.onBefore		onAfter			: null,		//	scroll.onAfter		onEnd			: null		//	scroll.onEnd	}}, {	debug			: false,	onWindowResize	: &quot;throttle&quot;,	events			: {		prefix			: &quot;&quot;,		namespace		: &quot;cfs&quot;	},	wrapper			: {		element			: &quot;div&quot;,		classname		: &quot;caroufredsel_wrapper&quot;	},	classnames		: {		selected		: &quot;selected&quot;,		hidden			: &quot;hidden&quot;,		disabled		: &quot;disabled&quot;,		paused			: &quot;paused&quot;,		stopped			: &quot;stopped&quot;	}});</pre>					<br />					<h4 id="_noncircular">A non-circular, non-infinite carousel.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	circular	: false,	infinite	: false});</pre>					<br />					<h4 id="_responsive1">Responsive carousels.</h4><pre class="brush: jscript;">//	centring the visible items$(&quot;#foo&quot;).carouFredSel({	width	: &quot;100%&quot;});//	resizing 5 items$(&quot;#foo&quot;).carouFredSel({	responsive	: true,	items		: 5});//	resizing the visible items to a maximum width of 200px//	resizing the item-height to 50% of the item-width$(&quot;#foo&quot;).carouFredSel({	responsive	: true,	items		: {		width		: 200,		height		: &quot;50%&quot;,		visible		: {			min			: 1,			max			: 10		}	}});</pre>					<br />					<h4 id="_up3items">A carousel scrolling 3 items up.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: 3,	direction	: &quot;up&quot;});</pre>					<br />					<h4 id="_highesthight">A carousel as high as the highest item.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	height		: &quot;auto&quot;,	items		: {		height		: &quot;auto&quot;	}});</pre>					<br />					<h4 id="_highestvisihight">A carousel as high as the highest visible item.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	height		: &quot;auto&quot;,	items		: {		height		: &quot;variable&quot;	}});</pre>					<br />					<h4 id="_alignrigh">Align the items to the right.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	width		: 400,	align		: &quot;right&quot;,	items		: {		visible		: 3,		width		: 100	}});//	align to the left$(&quot;#foo&quot;).carouFredSel({	align		: &quot;left&quot;});//	align centered$(&quot;#foo&quot;).carouFredSel({	align		: &quot;center&quot;});</pre>					<br />					<h4 id="_padding">Add padding around the items.</h4><pre class="brush: jscript;">//	50px around the items$(&quot;#foo&quot;).carouFredSel({	padding		: 50});//	50px left and right of the items$(&quot;#foo&quot;).carouFredSel({	padding		: [0, 50]});//	10px top, 20px right, 30px bottom and 40px left of the items$(&quot;#foo&quot;).carouFredSel({	padding		: [10, 20, 30, 40]});</pre>					<br />					<h4 id="_synchronise">Synchronise a second carousel.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	synchronise	: &quot;#foo_2&quot;});//	synchronise without inheriting the scrolling options$(&quot;#foo&quot;).carouFredSel({	synchronise	: [&quot;#foo_2&quot;, false]});//	synchronise without inheriting the scrolling options and scroll in the opposite direction$(&quot;#foo&quot;).carouFredSel({	synchronise	: [&quot;#foo_2&quot;, false, false]});//	synchronise with a deviation of 2 items$(&quot;#foo&quot;).carouFredSel({	synchronise	: [&quot;#foo_2&quot;, true, true, 2]});</pre>					<br />					<h4 id="_synchronise2">Synchronise multiple carousels.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	synchronise	: [		[&quot;#foo_2&quot;, true, true, 0],		[&quot;#foo_3&quot;, false, false, 0],		[&quot;#foo_4&quot;, true, false, 2]	]});</pre>					<br />					<h4 id="_cookie">Start carousel at its last viewed position using cookies.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	cookie	: true});//	specify a name for the cookie$(&quot;#foo&quot;).carouFredSel({	cookie	: &quot;cookie_name&quot;});</pre>					<br />					<h4 id="_nominimum">Create a carousel, even if there aren't enough items.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: 10,		minimum		: 1	}});</pre>					<br />					<h4 id="_start3">Start the carousel at the third item.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		start		: 2	}});</pre>					<br />					<h4 id="_startselector">Start the carousel at an item found by a jQuery selector.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		start		: &quot;#foo li:first&quot;	}});</pre>					<br />					<h4 id="_starthash">Start the carousel at the url-hashtag.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		start		: true	}});</pre>					<br />					<h4 id="_startrandom">Start the carousel at a random item.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		start		: &quot;random&quot;	}});</pre>					<br />					<h4 id="_startarray">Test for multiple values to start the carousel.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		start		: [true, &quot;li.start&quot;, &quot;random&quot;]	}});</pre>					<br />					<h4 id="_varivisible">Show a variable number of visible items.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;variable&quot;	}});</pre>					<br />					<h4 id="_minmaxvisi">Limit the number of visible items.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	height		: &quot;auto&quot;,	items		: {		visible		: {			min			: 2,			max			: 4		}	}});</pre>					<br />					<h4 id="_oneplus">Show more items than fits the available size.</h4><pre class="brush: jscript;">//	one item more$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;+1&quot;	}});//	two items more$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;+2&quot;	}});</pre>					<br />					<h4 id="_oneminus">Show less items than fits the available size.</h4><pre class="brush: jscript;">//	one item less$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;-1&quot;	}});//	two items less$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;-2&quot;	}});</pre>					<br />					<h4 id="_odd">Show the number that will fit and decreased to an odd number.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;odd&quot;	}});//	same thing but increased by two$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;odd+2&quot;	}});//	same thing but decreased by two$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;odd-2&quot;	}});</pre>					<br />					<h4 id="_even">Show the number that will fit and decreased to an even number.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;even&quot;	}});//	same thing but increased by two$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;even+2&quot;	}});//	same thing but decreased by two$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: &quot;even-2&quot;	}});</pre>					<br />					<h4 id="_dynavisi">Dynamically calculate the number of visible items.</h4><pre class="brush: jscript;">//	always show an odd number of items, overlapping the sides$(&quot;#foo&quot;).carouFredSel({	width: 600,	align: &quot;center&quot;,	items		: {		visible		: function( num ) {			if (num % 2 == 0) {				return num + 1;			} else {				return num + 2;			}		}	}});</pre>					<br />					<h4 id="_filtervisi">Filter the visible items</h4><pre class="brush: jscript;">//	show two items with class=&quot;foo&quot;.$(&quot;#foo&quot;).carouFredSel({	items		: {		visible		: 2,		filter		: &quot;.foo&quot;	}});//	ignore hidden items$(&quot;#foo&quot;).carouFredSel({	items		: {		filter		: &quot;:visible&quot;	}});</pre>					<br />					<h4 id="_scroll1">Scroll only one item.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	items		: 5,	scroll		: 1});</pre>					<br />					<h4 id="_scrollpage">Scroll to the first item in the previous/next &quot;page&quot;.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;page&quot;	}});</pre>					<br />					<h4 id="_scrollminus">Scroll less items than visible.</h4><pre class="brush: jscript;">//	one item less$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;-1&quot;	}});//	two items less$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;-2&quot;	}});</pre>					<br />					<h4 id="_scrollplus">Scroll more items than visible.</h4><pre class="brush: jscript;">//	one item more$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;+1&quot;	}});//	two items more$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;+2&quot;	}});</pre>					<br />					<h4 id="_scrollodd">Scroll the number of visible items decreased to an odd number.</h4><pre class="brush: jscript;">//	one item less$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;odd&quot;	}});//	same thing but increased by two$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;odd+2&quot;	}});//	same thing but decreased by two$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;odd-2&quot;	}});</pre>					<br />					<h4 id="_scrolleven">Scroll the number of visible items decreased to an even number.</h4><pre class="brush: jscript;">//	one item less$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;even&quot;	}});//	same thing but increased by two$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;even+2&quot;	}});//	same thing but decreased by two$(&quot;#foo&quot;).carouFredSel({	scroll		: {		items		: &quot;even-2&quot;	}});</pre>					<br />					<h4 id="_fxeasing">Scroll using a custom effect and easing-animation during 1 second.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		fx			: &quot;crossfade&quot;,		easing		: &quot;linear&quot;,		duration	: 1000	}});</pre>					<br />					<h4 id="_pauseonhover">Pause an automatically scrolled carousel onMouseOver.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		pauseOnHover: true	}});</pre>					<br />					<h4 id="_pauseonhoverresume">Pause an automatically scrolled carousel onMouseOver and resume onMouseOut.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		pauseOnHover: &quot;resume&quot;	}});</pre>					<br />					<h4 id="_pauseonhoverimmediate">Immediately pause the transition onMouseOver.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		pauseOnHover: &quot;immediate&quot;	}});</pre>					<br />					<h4 id="_pauseonhoverresumeimmediate">Immediately pause the transition onMouseOver and resume onMouseOut.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		pauseOnHover: &quot;immediate-resume&quot;	}});</pre>					<br />					<h4 id="_mousewheel">Scroll three items using the mousewheel. jQuery.mousewheel-plugin required.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	mousewheel	: true});</pre>					<br />					<h4 id="_mousewheel3">Scroll three items using the mousewheel. jQuery.mousewheel-plugin required.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	mousewheel	: {		items		: 3	}});</pre>					<br />					<h4 id="_wipe">Scroll by swiping (on touch-devices), or dragging (using the mouse). jQuery.touchSwipe-plugin required.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	wipe		: {		onTouch		: true,		onMouse		: true	}});</pre>					<br />					<h4 id="_queue">Place the transition in a queue if the carousel is currently being animated.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		queue		: true	}});</pre>					<br />					<h4 id="_conditions">Use a custom function to test if the carousel should scroll.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		conditions	: function( direction ) {			if ( $(this).hasClass( &quot;active&quot; ) ) {				return true;			} else {				return false;			}		}	}});</pre>					<br />					<h4 id="_callbackfunction">Use callback function to make stuff happen.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	scroll		: {		onBefore	: function( data ) {			data.items.old.removeClass( &quot;active&quot; );		},		onAfter		: function( data ) {			data.items.new.addClass( &quot;active&quot; );		},		onEnd		: function() {			$(this).addClass( &quot;at-end&quot; );		}	}});</pre>					<br />					<h4 id="_nonauto">A carousel that doesn't scroll automatically.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	auto		: false});</pre>					<br />					<h4 id="_pauseduration">Wait three seconds between scrolling.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	auto		: {		timeoutDuration	: 3000	}});</pre>					<br />					<h4 id="_delay">Wait five seconds extra before the first scroll.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	auto		: {		delay		: 5000	}});</pre>					<br />					<h4 id="_button">Toggle a play/pause button.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	auto		: {		button		: &quot;.play-bnt&quot;	}});</pre>					<br />					<h4 id="_progress">Create a progressbar.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	auto		: {		progress		: {			bar				: &quot;#foo_bar&quot;,			updater			: function( percentage ) {				$(this).width( percentage + "%" );			}		}	}});</pre>					<br />					<h4 id="_button">Scroll using buttons.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	prev		: &quot;.prev-btn&quot;,	next		: &quot;.next-btn&quot;});</pre>					<br />					<h4 id="_buttonskeys">Scroll using buttons and keys.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	prev		: {		button		: &quot;.prev-btn&quot;,		key			: &quot;left&quot;	},	next		: {		button		: &quot;.next-btn&quot;,		key			: &quot;right&quot;	}});</pre>					<br />					<h4 id="_pagination">Scroll using pagination.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: &quot;.pager&quot;});</pre>					<br />					<h4 id="_paginationkeys">Scroll using pagination and keys.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: {		container	: &quot;.pager&quot;,		keys		: true	}});</pre>					<br />					<h4 id="_buttonskeys">Get the HTML-elements for the buttons and the container dynamically.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	prev		: {		button		: function() {			return $(this).parents(&quot;.wrapper&quot;).find(&quot;.prev&quot;);		}	},	next		: {		button		: function() {			return $(this).parents(&quot;.wrapper&quot;).find(&quot;.next&quot;);		}	},	pagination	: {		container	: function() {			return $(this).parents(&quot;.wrapper&quot;).find(&quot;.pager&quot;);		}	}});</pre>					<br />					<h4 id="_triggerevent">Specify the event to trigger the buttons (&quot;click&quot; by default).</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	prev		: {		button		: &quot;.prev-btn&quot;,		event		: &quot;mouseenter&quot;	},	next		: {		button		: &quot;.next-btn&quot;,		event		: &quot;mouseleave&quot;	},	pagination	: {		container	: &quot;.pager&quot;,		event		: &quot;mousedownr&quot;	}});</pre>					<br />					<h4 id="_custompagination">Create a custom pagination.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: {		container		: &quot;.pager-wrapper&quot;,		anchorBuilder	: function( nr ) {			return &quot;&lt;span&gt;&quot; + nr + &quot;&lt;/span&gt;&quot;;		}	}});</pre>					<br />					<h4 id="_thumbnailpagination">Create a thumbnail pagination.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: {		container		: &quot;.pager-wrapper&quot;,		anchorBuilder	: function( nr ) {			var src = $(this).attr( &quot;src&quot; );				src = src.replace( &quot;/large/&quot;, &quot;/small/&quot; );			return '&lt;img src=&quot;' + src + '&quot; /&gt;';		}	}});</pre>					<br />					<h4 id="_textualpagination">Create textual pagination.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: {		container		: &quot;.pager-wrapper&quot;,		anchorBuilder	: function( nr ) {			var title = $(this).find( &quot;h2&quot; ).text();			return &quot;&lt;span&gt;&quot; + title + &quot;&lt;/span&gt;&quot;;		}	}});</pre>					<br />					<h4 id="_nopagination">Use existing elements for the pagination.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: {		container		: &quot;.pager-wrapper&quot;,		anchorBuilder	: false	}});</pre>					<br />					<h4 id="_deviatepagination">Deviate the selected bullet in the pagination.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	pagination	: {		container		: &quot;.pager-wrapper&quot;,		deviation		: 2	}});</pre>					<br />					<h4 id="_override">Override options for every way of scrolling.</h4><pre class="brush: jscript;">$(&quot;#foo&quot;).carouFredSel({	auto		: {		items		: 1	},	prev		: {		items		: 2	},	next		: {		items		: 3	},	swipe		: {		items		: 4	},	mousewheel	: {		items		: 5	}});</pre>				</div> <!-- /usage --><?php	include_once '../functies/layout_post.php';?>