<?php	function select_number_options($start = 1, $eind = 30, $s = false, $d = 0) {		$str = '';		for ($e = $start; $e <= $eind; $e++) {			if ($e == $s) {				$sel = ' selected="selected"';			} else {				$sel = '';			}			$str .= '													<option'.$sel.' value="'.($e+$d).'">'.$e.'</option>';		}		return $str;	}	function get_error($m) {		return '									<div class="error closed">										'.$m.'									</div>';	}	function get_table($m, $t = '') {		return '									'.$t.'<table cellpadding="0" cellspacing="0" border="0" class="a">'.$m.'									'.$t.'</table>';	}	function width_height_item($d = 'width') {		global $error_number, $dontknow, $inpixels;				$s1 = ($d == 'width') ? 'widest'	: 'highest';		$s2 = ($d == 'width') ? 'vertical'	: 'horizontal';		$s3 = ($d == 'width') ? 'width' 	: 'height';		return '								<div class="item is_intermediate">									<p class="q">The '.$d.' of the carousel should be...</p>									<table cellpadding="0" cellspacing="0" border="0" class="a">										<tr>											<td><input name="r_'.$d.'" value="" type="radio" class="rdo" checked="checked" /></td>											<td>'.$dontknow.'</td>										</tr>										<tr>											<td><input name="r_'.$d.'" value="fixed" type="radio" class="rdo" /></td>											<td>A fixed or fluid size.</td>										</tr>										<tr>											<td><input name="r_'.$d.'" value="variable" type="radio" class="rdo" /></td>											<td>Variable (carousel resizes to fit the visible item(s)).</td>										</tr>										<tr class="is_'.$s2.'">											<td><input name="r_'.$d.'" value="auto" type="radio" class="rdo" /></td>											<td>The '.$s3.' of the '.$s1.' item.</td>										</tr>									</table>								</div> <!-- /item -->								<div class="hidden_item is_'.$d.'">									<p class="q">Specify the desired '.$d.' for the carousel.</p>'.get_table('										<tr>											<td><code>'.$d.':</code><input name="a_'.$d.'" value="" type="text" class="txt number" size="20" /></td>											<td><select name="s_'.$d.'_type" size="1">													<option value="">px</option>													<option value="percent">%</option>												</select><code>;</code></td>										</tr>').$error_number.'								</div> <!-- /is_'.$d.' -->';	}	function width_height_items_item($d = 'width') {		global $error_number, $dontknow, $inpixels;		return '								<div class="item is_intermediate">									<p class="q">Do all the items in your carousel have the same '.$d.'?</p>'.get_table('										<tr>											<td><input name="r_'.$d.'_items" value="" type="radio" class="rdo" checked="checked" /></td>											<td>'.$dontknow.'</td>										</tr>										<tr>											<td><input name="r_'.$d.'_items" value="fixed" type="radio" class="rdo" /></td>											<td>Yes, I want to specify a fixed '.$d.' for the items.</td>										</tr>										<tr>											<td><input name="r_'.$d.'_items" value="variable" type="radio" class="rdo" /></td>											<td>No, the items have variable '.$d.'s.</td>										</tr>').'								</div> <!-- /item -->								<div class="hidden_item is_'.$d.'_items_fixed">									<p class="q">Specify the desired '.$d.' for the items.</p>'.get_table('										<tr>											<td><code>items.'.$d.':</code><input name="a_'.$d.'_items" value="" type="text" class="txt number" size="20" /></td>											<td><select name="s_'.$d.'_items_type" size="1">													<option value="">px</option>													<option value="percent">%</option>												</select><code>;</code></td>										</tr>').$error_number.'								</div> <!-- /item -->';	}	function object_items($postfix = '_scroll', $object = '', $element = 'it', $items = true, $auto = true) {		if (strlen($object) > 0) {			$eq1 = ' for scrolling '.$object;			$eq2 = ' <small>(when scrolling '.$object.')</small>'; 			$eq3 = ' <small>(to be executed before scrolling '.$object.')</small>';			$eq4 = ' <small>(to be executed after scrolling '.$object.')</small>';		} else {			$eq1 = '';			$eq2 = '';			$eq3 = '';			$eq4 = '';		}		if (strlen($element) > 0) $element = ' over '.$element;		$str = '								<div class="item">'.override_what_options($postfix, $eq1, $element).'								</div> <!-- /item -->';		if ($items) {			$str .= '								<div class="hidden_item is_items'.$postfix.'">'.items_item($postfix, $eq2).'								</div> <!-- /is_items'.$postfix.' -->';		}		$str .= '								<div class="hidden_item is_fx'.$postfix.'">'.fx_item($postfix, $eq2).'								</div> <!-- /is_items'.$postfix.' -->								<div class="hidden_item is_easing'.$postfix.'">'.easing_item($postfix, $eq2).'								</div> <!-- /is_easing'.$postfix.' -->								<div class="hidden_item is_duration'.$postfix.'">'.duration_item($postfix, $eq2).'								</div> <!-- /is_duration'.$postfix.' -->';		if ($auto) {			$str .= '								<div class="is_auto">									<div class="hidden_item is_pauseOnHover'.$postfix.'">'.pauseOnHover_item($postfix, $element).'									</div> <!-- /is_pauseOnHover'.$postfix.' -->								</div>';		}		return $str;	}	function override_what_options($postfix = '_scroll', $extra_question = '', $poh = false, $its = true) {		$poh = ($poh) ? '										<tr>											<td><div class="is_auto"><input name="r_override'.$postfix.'" value="pauseOnHover" type="checkbox" class="cbx" /></div></td>											<td><div class="is_auto">Whether the carousel should <strong>pause on hover</strong>'.$poh.'.</div></td>										</tr>' : '';		$its = ($its) ? '										<tr>											<td><input name="r_override'.$postfix.'" value="items" type="checkbox" class="cbx" /></td>											<td>The number of <strong>items</strong> to scroll.</td>										</tr>' : '';		return '									<p class="q">What of the following options do you want to specify'.$extra_question.'?</p>									<p>All options you do not specify will be inherited from the options for general scrolling.</p>'.get_table('										'.$its.'										<tr>											<td><input name="r_override'.$postfix.'" value="fx" type="checkbox" class="cbx" /></td>											<td>The <strong>effect</strong> to use for the transition.</td>										</tr>										<tr>											<td><input name="r_override'.$postfix.'" value="duration" type="checkbox" class="cbx" /></td>											<td>The <strong>duration</strong> of the transition.</td>										</tr>										<tr>											<td><div class="is_intermediate"><input name="r_override'.$postfix.'" value="easing" type="checkbox" class="cbx" /></div></td>											<td><div class="is_intermediate">The <strong>easing</strong> function for the transition.</div></td>										</tr>'.$poh);	}	function items_item($postfix = '_scroll', $extra_question = '') {		if ($postfix == '_scroll') {			$samea = '											<td><input name="r_items'.$postfix.'" value="" type="radio" class="rdo" checked="checked" /></td>											<td>The same as visible.</td>										</tr>										<tr>											<td><input name="r_items'.$postfix.'" value="fixed" type="radio" class="rdo" /></td>';			$pagi = '<p class="is_pagination" style="display: none;">When using the pagination, the number of items to scroll will be calculated depending on the destination page.</p>';		} else {			$samea = '';			$pagi = '';		}		return '									<p class="q">How many items should be scrolled at a time'.$extra_question.'?</p>									'.$pagi.get_table('										<tr>'.$samea.'											<td><code>'.substr($postfix, 1).'.items:</code><select name="a_items'.$postfix.'" size="1">													'.select_number_options().'												</select><code>;</code></td>										</tr>');	}	function fx_item($postfix = '_scroll', $extra_question = '') {		return '									<p class="q">What effect would you like to use for the transition'.$extra_question.'?</p>									'.$pagi.get_table('										<tr>'.$samea.'											<td><code>'.substr($postfix, 1).'.fx:</code><select name="a_fx'.$postfix.'" size="1">													<option value="none">none</option>													<option value="" selected="selected">scroll</option>													<option value="directscroll">directscroll</option>													<option value="fade">fade</option>													<option value="crossfade">crossfade</option>													<option value="cover">cover</option>													<option value="cover-fade">cover-fade</option>													<option value="uncover">uncover</option>													<option value="uncover-fade">uncover-fade</option>												</select><code>;</code></td>										</tr>');	}	function easing_item($postfix = '_scroll', $extra_question = '') {		if ($postfix == '_scroll') {			$value = '';		} else {			$value = 'swing';		}		return '									<p class="q">What easing function do you want to use for the transition'.$extra_question.'?</p>'.get_table($samea.'										<tr>											<td><input name="r_transition'.$postfix.'" value="basic" type="radio" checked="checked" class="rdo" /></td>											<td>Basic + build-in transitions</td>											<td><select name="a_transition_basic'.$postfix.'" size="1">													<option value="'.$value.'">swing</option>													<option value="linear">linear</option>													<option value="quadratic">quadratic</option>													<option value="cubic">cubic</option>													<option value="elastic">elastic</option>												</select></td>										</tr>										<tr>											<td><input name="r_transition'.$postfix.'" value="easing" type="radio" class="rdo" /></td>											<td>Easing plugin transitions</td>											<td><select name="a_transition_easing'.$postfix.'" size="1">													<option value="easeInQuad">easeInQuad</option>													<option value="easeOutQuad">easeOutQuad</option>													<option value="easeInOutQuad">easeInOutQuad</option>													<option value="easeInCubic">easeInCubic</option>													<option value="easeOutCubic">easeOutCubic</option>													<option value="easeInOutCubic">easeInOutCubic</option>													<option value="easeInQuart">easeInQuart</option>													<option value="easeOutQuart">easeOutQuart</option>													<option value="easeInOutQuart">easeInOutQuart</option>													<option value="easeInQuint">easeInQuint</option>													<option value="easeOutQuint">easeOutQuint</option>													<option value="easeInOutQuint">easeInOutQuint</option>													<option value="easeInSine">easeInSine</option>													<option value="easeOutSine">easeOutSine</option>													<option value="easeInOutSine">easeInOutSine</option>													<option value="easeInExpo">easeInExpo</option>													<option value="easeOutExpo">easeOutExpo</option>													<option value="easeInOutExpo">easeInOutExpo</option>													<option value="easeInCirc">easeInCirc</option>													<option value="easeOutCirc">easeOutCirc</option>													<option value="easeInOutCirc">easeInOutCirc</option>													<option value="easeInElastic">easeInElastic</option>													<option value="easeOutElastic">easeOutElastic</option>													<option value="easeInOutElastic">easeInOutElastic</option>													<option value="easeInBack">easeInBack</option>													<option value="easeOutBack">easeOutBack</option>													<option value="easeInOutBack">easeInOutBack</option>													<option value="easeInBounce">easeInBounce</option>													<option value="easeOutBounce">easeOutBounce</option>													<option value="easeInOutBounce">easeInOutBounce</option>												</select> (only available when you include the jquery.easing plugin)</td>										</tr>');	}	function duration_item($postfix = '_scroll', $extra_question = '') {		global $error_number, $dontknow;		$value = ($postfix == '_scroll') ? '500' : '';		if ($postfix == '_prev' || 			$postfix == '_next' || 			$postfix == '_pagination'		) {			$autob = '											<td><input name="r_duration'.$postfix.'" value="number" type="radio" checked="checked" class="rdo" /></td>';			$autoa = '				  						<tr>											<td><input name="r_duration'.$postfix.'" value="auto" type="radio" class="rdo" /></td>											<td colspan="4">Calculate a <em>speed</em> for the transition in stead of using a <em>duration</em>.</td>				  						</tr>';		} else if ($postfix == '_scroll') {			$autob = '											<td><input name="r_duration'.$postfix.'" value="fixed" type="radio" class="rdo" /></td>';			$autoa = '				  						<tr>											<td><input name="r_duration'.$postfix.'" value="" type="radio" checked="checked" class="rdo" /></td>											<td colspan="4">'.$dontknow.'</td>				  						</tr>';		} else {			$autob = '';			$autoa = '';		}		return '									<p class="q">How long would you like for the transition to run'.$extra_question.'?</p>									<p>If the value is less then <code>10</code>, the plugin will calculate a <em>speed</em> for the transition (the value represents the number of pixels scrolled per millisecond). This might be desirable when scrolling items with variable sizes.</p>'.get_table($autoa.'										<tr>'.$autob.'											<td><code>'.substr($postfix, 1).'.duration:</code></td>											<td><input name="a_duration'.$postfix.'" value="'.$value.'" type="text" class="txt number" size="20" /></td>											<td><code>;</code></td>											<td>(milliseconds)</td>										</tr>').$error_number;	}	function callback_item($postfix = '_auto', $extra_question = '', $function = 'onBefore') {		global $error_function;		$duration 	= ($function == 'onBefore') ? ', duration' : '';		$error 		= '';		$class 		= '';		$params 	= '';		$an			= 'an';		switch($function) {			case 'onEnd':				break;			case 'conditions':				$error 	= $error_function;				$class 	= ' class="function"';				$an = 'a';				break;			case 'onBefore':			case 'onAfter':				$params	= 'oldItems, newItems, newSizes'.$duration;				break;			default:				$params	= 'percentage, duration';				break;		}		return '									<p class="q">Specify '.$an.' &quot;'.$function.'&quot; function'.$extra_question.'</p>'.get_table('										<tr>											<td><code>'.substr($postfix, 1).'.'.$function.': function('.$params.') {</code></td>										</tr>										<tr>											<td><textarea name="a_'.$function.$postfix.'"'.$class.' rows="15" cols="30" style="margin-left: 20px;"></textarea></td>										</tr>										<tr>											<td><code>}</code></td>										</tr>').$error;	}	function pauseOnHover_item($postfix = '_scroll', $extra_question = ' over it') {		if ($postfix == '_scroll') {			$check = ' checked="checked"';			$value = '';		} else {			$check = '';			$value = 'false';		}		return '									<p class="q">Do you want the timeout between transitions to be paused when the cursor hovers'.$extra_question.'?</p>'.get_table('										<tr>											<td><input name="a_pauseOnHover'.$postfix.'" value="'.$value.'" type="radio" class="rdo"'.$check.' /></td>											<td>No, do not pause the timeout.</td>										</tr>										<tr>											<td><input name="a_pauseOnHover'.$postfix.'" value="true" type="radio" class="rdo" /></td>											<td>Yes, pause the timeout when the cursor hovers'.$extra_question.'.</td>										</tr>');	}	function prev_next_btn_key($o) {		global $valid_selector, $error_selector;		$d = ($o == 'prev') ? 'backward' : 'forward';		return '								<div class="item">									<p class="q">What button can the user press to make the carousel scroll '.$d.'?</p>									<p>'.$valid_selector.' <em>For example:</em> <code>#'.$o.'_btn</code> or <code>#foo &gt; a.'.$o.'</code>.<br />										Leave this field empty if you do not want the user to scroll the carousel '.$d.' by pressing a button.</p>'.get_table('										<tr>											<td><code>$(&quot;</code></td>											<td><input name="a_button_'.$o.'" value="" type="text" class="txt selector" size="20" /></td>											<td><code>&quot;);</code></td>										</tr>').$error_selector.'								</div> <!-- /item -->								<div class="item">									<p class="q">What keyboard key can the user press to make the carousel scroll '.$d.'?</p>									<p>Place your cursor in the field below and press the desired key on your keyboard.<br />										Leave this field empty if you do not want the user to scroll the carousel '.$d.' by pressing a key.</p>'.get_table('										<tr>											<td><code>key:</code></td>											<td><input name="a_key_'.$o.'" value="" type="text" class="txt" size="20" /></td>											<td><code>;</code></td>										</tr>').'								</div> <!-- /item -->';	}	$inpixels		= '<code>; // in pixels</code>';	$dontknow		= 'I don\'t know (let the plugin figure it out).';	$valid_selector = 'Use a valid jQuery-selector to select the HTML element(s).';	$error_selector = get_error('<p class="alert">Uh oh, that does not look like a valid jQuery-selector, more info about selectors <a href="http://api.jquery.com/category/selectors/" target="_blank">here</a>.</p>');	$error_number	= get_error('<p class="alert">Hold on, that\'s not a number...</p>');	$error_function	= get_error('<p class="alert">Uhmmm, doesn\'t this function need to &quot;return&quot; a value?</p>');	$description = 'The configuration robot will guide you through configurating the carouFredSel plugin. He\'s programmed to make your life easy.';	$keywords = 'configuration, robot, carousel, jquery, options, settings, wizzard, guide, tool';	$title = 'Configuration Robot';	$links = '		<link rel="stylesheet" type="text/css" media="all" href="css/config_robot.css" />';	$scripts = '		<script type="text/javascript" language="javascript" src="js/config_robot.js"></script>';	include_once 'functies/layout_pre.php';?>				<div class="transparent robot">					<div id="robot_intro">						<h1 class="robot">Configuration <span>Robot</span></h1>						<p>Not sure how to make the best use of all the options and settings the carouFredSel-plugin offers?<br />							Don't worry, I'm <strong>the incredible configuration robot</strong> and I'm programmed to make your life easy!</p>						<p>Simply answer my questions and we'll figure it out together...<br />							And always remember: all options are OPTIONAL. So if you don't know the answer, simply skip to the next question!</p>												<p><a href="#robot_questions" class="button">START &raquo;</a></p>					</div> <!-- /robot_intro -->					<div id="robot_questions">						<div class="item chapter">							<h4>Okay then, lets start!</h4>							<div class="questions">								<div class="item">									<p class="q">Do you want to show the intermediate questions?</p>									<table cellpadding="0" cellspacing="0" border="0" class="a">										<tr>											<td><input name="intermediate_configuration" value="intermediate" type="checkbox" checked="checked" class="cbx" /></td>											<td>Yes, show the intermediate questions.</td>										</tr>									</table>								</div> <!-- /item -->								<div class="item">									<p class="q">What HTML element(s) do you want to turn into a carousel?</p>									<p><?php echo $valid_selector; ?> <em>For example:</em> <code>#foo</code> or <code>.foo ul</code>.</p>									<table cellpadding="0" cellspacing="0" border="0" class="a">										<tr>											<td><code>$(&quot;</code> <input name="a_selector" value="" type="text" class="txt selector" size="20" /> <code>&quot;).carouFredSel();</code></td>										</tr>									</table><?php	echo $error_selector;?>								</div> <!-- /item -->								<div class="item">									<p class="q">Do you want a horizontal or a vertical carousel?</p>									<table cellpadding="0" cellspacing="0" border="0" class="a">										<tr>											<td><input name="a_hor_ver" value="h" type="radio" class="rdo" checked="checked" /></td>											<td>Horizontal</td>										</tr>				