<?php	$description = 'Examples of the carouFredSel-plugin on how to dynamically add and remove items to/from a carousel.';	$keywords = 'examples, add, insert, remove, items, carousel, custom, event, insertitem, removeitem';	$title = 'Dynamically add and remove items - Examples';	$script_foo1 = '$("#foo1").carouFredSel({	auto	: false,	prev	: "#foo1_prev",	next	: "#foo1_next",	scroll 	: 5}).find("li").click(function() {	$(this).animate({		opacity 	: 0	}, 500).animate({		width		: 0,		margin		: 0,		borderWidth	: 0	}, 500, function() {		$("#foo1").trigger("removeItem", $(this));	});	}).css("cursor", "pointer");';	$script_foo2 = 'function getOptions(items) {	var i = $("input[name=s_items]").val(),		p = $("select[name=s_position]").val(),		o = $("select[name=s_order]").val(),		d = $("select[name=s_deviation]").val();	if (o == "false")	o = false;	if (o == "true")	o = true;		if (items)	return [i, p, o, d];	else		return [p, o, d];}$("#foo2").carouFredSel({	items	: 4,	auto	: false,	prev	: "#foo2_prev",	next	: "#foo2_next",	scroll	: {		items		: 2,		duration	: 1500	}});$("#insert_btn").click(function(e) {	var arr = getOptions(true);	$("#foo2").trigger("insertItem", arr);	e.preventDefault();});$("#remove_btn").click(function(e) {	var arr = getOptions(false);	$("#foo2").trigger("removeItem", arr);	e.preventDefault();});';	$scripts = '		<script type="text/javascript" language="javascript">			$(function() {				'.$script_foo1.$script_foo2.'			});		</script>		<script type="text/javascript" language="javascript" src="../js/add_remove_items.js"></script>';	include_once '../functies/layout_pre.php';?>				<h1>Dynamically add and remove items</h1>				<p class="no_marg"><strong>Remove items:</strong><br />					Click on an item to remove it from the carousel (using a smooth animation).</p><?php	$E_script = $script_foo1;	$E_element = 'ul';	include 'scripts/list_carousel.php';?><?php	if ( $showAds )	{		echo '			<div class="adsense_center_728">' . $adsense_balk . '			</div>';	}?>				<br />				<br />				<p><strong>Insert and remove items:</strong><br />					To clarify what is actually happening when adding or removing items, the following carousel has its <code>overflow</code> reset to <code>visible</code>.</p>				<br />				<div class="half" id="insert_remove">					<div class="left" style="padding-left: 75px; width: 425px;">						<table cellpadding="0" cellspacing="0" border="0">							<tr>								<td><code>i</code> = <code>items</code></td>								<td> = </td>								<td><code id="s_items"></code>									<input name="s_items" type="hidden" class="txt" value="" /></td>							</tr>							<tr>								<td><code>p</code> = <code>position</code></td>								<td> = </td>								<td><select name="s_position" size="1">										<optgroup label="String:">											<option value="end" selected="selected">&quot;end&quot; (default)</option>										</optgroup>										<optgroup label="String (jQuery-selector):">											<option value="#foo2 li:first">&quot;#foo2 li:first&quot;</option>											<option value="#foo2 li:last">&quot;#foo2 li:last&quot;</option>										</optgroup>										<optgroup label="jQuery-object:">											<option value="#foo2 li:first">$(&quot;#foo2 li:first&quot;)</option>											<option value="#foo2 li:last">$(&quot;#foo2 li:last&quot;)</option>										</optgroup>										<optgroup label="Number:" id="s_position_number">											<option value="0">0</option>										</optgroup>									</select></td>							</tr>							<tr>								<td><code>o</code> = <code>origOrder</code></td>								<td> = </td>								<td><select name="s_order" size="1">										<option value="" selected="selected">(auto select)</option>										<option value="true">true</option>										<option value="false">false</option>									</select></td>							</tr>							<tr>								<td><code>d</code> = <code>deviation</code></td>								<td> = </td>								<td><select name="s_deviation" size="1">										<option value="-1">-1</option>										<option value="-2">-2</option>										<option value="-3">-3</option>										<option value="-4">-4</option>										<option value="-5">-5</option>										<option value="0" selected="selected">0</option>										<option value="1">1</option>										<option value="2">2</option>										<option value="3">3</option>										<option value="4">4</option>										<option value="5">5</option>									</select></td>							</tr>						</table>					</div> <!-- /left -->					<div class="right" style="padding: 40px 25px 0 0; width: 425px;">						<p>Set the options on the left and insert or remove an item to/from the carousel by clicking the buttons below.</p>						<p><a id="insert_btn" class="button" style="float: left;" href="#"><span>insertItem(i,p,o,d);</span></a>							<a id="remove_btn" class="button" style="float: left;" href="#"><span>removeItem(p,o,d);</span></a></p>					</div> <!-- /right -->					<div class="clearfix"></div>				</div> <!-- /half --><?php	$E_script = $script_foo2;	$E_element = 'ul';	$E_punt	= '';	$E_list = '123456';	include 'scripts/list_carousel.php';	if ( $showAds )	{		echo '			<div class="adsense_center_728">' . $adsense_balk . '			</div>';	}?>				<br /><?php	include 'index.php';	include_once '../functies/layout_post.php';?>