<?php	$example_ar = array(		array('Basic carousels'												, 'basic-carousels'),		array('Carousels scrolled by user interaction'						, 'user-interaction'),		array('Variable item-sizes + variable number of visible items'		, 'variable-visible-variable-size'),		array('Responsive / fluid carousels'								, 'responsive-carousels'),		array('Continuously scrolling carousels'							, 'continuously-scrolling'),		array('Using the callback functions'								, 'callback-functions'),		array('Using the custom events'										, 'custom-events'),		array('Intermediate carousel examples'								, 'intermediate-carousels'),		array('Dynamically add and remove items'							, 'add-remove-items'),		array('GET and (re)SET the configuration options'					, 'get-set-configuration'),		array('Carousels in combination with lightbox- and tooltip-plugins'	, 'carousel-lightbox-tooltip')	);	if (!isset($site)) {		header('location: '.$example_ar[0][1].'.php');		exit;	}	if ($site['root_rel'] != '') {		echo '				<div class="robot_container">';	}	echo '					<h4>More examples</h4>					<ul>';	foreach($example_ar as $e) {		$url = $site['root'].'examples/'.$e[1].'.php';		echo '						<li>';		if (item_selected($url))	echo '<span class="selected">'.$e[0].'.</span>';		else						echo '<a href="'.$url.'" title="'.$e[0].'">'.$e[0].'.</a>';		echo '</li>';	}	echo '						<li><a href="http://coolcarousels.frebsite.nl" title="inspiration slideshows and carousels" target="_blank">Cool Carousel examples (inspiration)</a></li>					</ul>';	if ($site['root_rel'] != '') {		echo $config_robot_button.'				</div> <!-- /robot_container -->';	}?>