<?php	$description = 'Examples of responsive, fluid, liquid, elastic jquery carousels.';	$keywords = 'responsive, fluid, liquid, elastic, intermediate, expert';	$title = 'Responsive carousels - Examples';	$script_foo1 = '$("#foo1").carouFredSel({	width	: "100%",	scroll	: 2});';	$script_foo2 = '$("#foo2").carouFredSel({	responsive	: true,	scroll		: {		fx			: "crossfade"	},	items		: {		visible		: 1,		width		: 870,		height		: "46%"	}});';	$script_foo3 = '$("#foo3").carouFredSel({	responsive	: true,	scroll		: 1,	items		: {		visible		: 5,		width		: 200	}});';	$script_foo4 = '$("#foo4").carouFredSel({	responsive	: true,	items		: {		width		: 200,		visible		: {			min			: 5,			max			: 10		}	}});';	$scripts = '		<script type="text/javascript" language="javascript">			$(function() {				$("#foo2 .slide div").show();				'.$script_foo1.$script_foo2.$script_foo3.$script_foo4.'			});		</script>';	include_once '../functies/layout_pre.php';?>				<h1>Responsive carousels</h1>				<p>Resize your browser, all the carousels on this page will adapt to the new available size.</p>				<br />				<p class="no_marg">A carousel centering the visibe items.</p><?php	$E_responsive = true;	$E_punt = '';	$E_script = $script_foo1;	include 'scripts/image_carousel.php';		if ( $showAds )	{		echo '				<div class="adsense_center_728">' . $adsense_balk . '				</div>';	}?>				<br />				<br />				<p>A responsive one-image gallery.</p><?php	$E_responsive = true;	$E_punt = '';	$E_script = $script_foo2;	include 'scripts/html_carousel.php';?>				<br />				<br />				<p>A carousel resizing a fixed number of visible items to fill the carousel.</p><?php	$E_responsive = true;	$E_element = 'ul';	$E_punt = '';	$E_script = $script_foo3;	include 'scripts/list_carousel.php';?>				<br />				<br />				<p>Restricting the number of visible items to a minimum and maximum amount.</p><?php	$E_responsive = true;	$E_element = 'ul';	$E_punt = '';	$E_script = $script_foo4;	include 'scripts/list_carousel.php';	if ( $showAds )	{		echo '				<div class="adsense_center_728">' . $adsense_balk . '				</div>';	}?>				<br /><?php	include 'index.php';	include_once '../functies/layout_post.php';?>