<?php$E_images = array(		'basketball.jpg',		'beachtree.jpg',		'cupcackes.jpg',		'hangmat.jpg',		'new_york.jpg',		'laundry.jpg',		'mom_son.jpg',		'picknick.jpg',		'shoes.jpg',		'paris.jpg',		'sunbading.jpg',		'yellow_couple.jpg'	);$E_html = array(		array('carousel_1.jpg', 'Infinity'			, 'A concept that in many fields refers to a quantity without bound or end.'),		array('carousel_2.jpg', 'Circular journey'	, 'An excursion in which the final destination is the same as the starting point.'),		array('carousel_3.jpg', 'jQuery'			, 'jQuery is a JavaScript library designed to simplify the client-side scripting.'),		array('carousel_4.jpg', 'Carousel'			, 'A carousel is an amusement ride consisting of a rotating circular platform with seats.')	);$E_list = 'carouFredSel: an infinite, circular jQuery carousel... ';function img_lijst($images, $start = 0, $eind = 0) {	global $site, $E_lightbox, $E_imgclass, $E_imgrel, $E_id, $E_variable_width, $E_variable_height;	$str = '';	$cls = ($E_imgclass) 	? ' class="'.$E_imgclass.'"' : '';	$rel = ($E_imgrel) 		? ' rel="'.$E_imgrel.'"' : '';	$tab = ($E_lightbox) 	? "\t" : '';	$exa = ($site['root_rel'] == '') ? 'examples/' : '';	if ($eind == 0) $eind = count($images);	for($e = $start; $e < $eind; $e++) {		$i		= $images[$e];		$i_siz	= getimagesize($site['root_rel'].'examples/images/small/'.$i);		$i_alt 	= get_image_alt($i);		if ($E_lightbox) $str .= '							<a'.$cls.$rel.' href="'.$site['root'].'examples/images/large/'.$i.'">';		$str .= '							'.$tab.'<img src="'.$site['root'].'examples/images/small/'.$i.'" alt="'.$i_alt.'" '.$i_siz[3].' />';		if ($E_lightbox) $str .= '							</a>';	}		return $str;}function list_lijst($list, $start = 0, $eind = 0) {	global $E_variable_width, $E_variable_height;	$str = '';	if ($eind == 0) $eind = strlen($list);	for ($e = $start; $e < $eind; $e++) {		$n = (rand(1, 3) * 100) - 50;		$a	 = array();		if ($E_variable_width)  $a[] = 'width: '.$n.'px;';		if ($E_variable_height) $a[] = 'height: '.$n.'px; margin-top: '.((80-$n)/2+6).'px;';		$s = (sizeof($a) > 0) ? ' style="'.implode(' ', $a).'"' : '';		$str .= '							<li'.$s.'> '.$list[$e].' </li>';	}	return $str;}function html_lijst($html, $start = 0, $eind = 0) {	global $site, $E_variable_width, $E_variable_height;	$str = '';	if ($eind == 0) $eind = count($html);	for($e = $start; $e < $eind; $e++) {		$h 		= $html[$e];		$i 		= $h[0];		$i_siz	= getimagesize($site['root_rel'].'examples/images/large/'.$i);		$i_alt	= get_image_alt($i);		$str .= '							<div class="slide">								<img src="'.$site['root'].'examples/images/large/'.$i.'" alt="'.$i_alt.'" '.$i_siz[3].' />								<div>									<h4>'.$h[1].'</h4>									<p>'.$h[2].'</p>								</div>							</div>';	}	return $str;}function make_pre_save($str, $t = "\t\t\t\t\t") {	$str = htmlentities($str);	if ($t == '') return $str;	return str_replace($t, "", $str);}?>